import React, { useEffect, useState} from 'react';
import './content.css'
import {response} from "express";

const user = {
    name: 'user1',
    password: '1234'
}

const news_url = 'http://localhost:5000/news'

const Content = () => {
    const [emailText, setEmailText] = useState('');
    const [passwordText, setPassword] = useState('');
    const [isAuth, setIsAuth] = useState(false);
    const [news, setNews] = useState([]);

    const handleChangeEmail = (e) => {
        setEmailText(e.target.value);
    };

    const handleChangePassword = (e) => {
        setPassword(e.target.value);
    };

    const onSignIn = () => {
        if (emailText === user.name && user.password === passwordText) {
            setIsAuth(true)
        }
    };

    const onLogout = () => {
        setIsAuth(false)
    }

    useEffect(() => {
        fetch(news_url)
            .then((res) => res.json())
            .then((res) => console.log(res))
    }, [])


    return (
        <div className="content">
            {!isAuth && (
                <div className="emailForm">
                    <input
                        className="emailField"
                        type="text"
                        value={emailText}
                        placeholder="Введите логин"
                        onChange={handleChangeEmail}
                    />
                    <input
                        className="emailField"
                        type="password"
                        onChange={handleChangePassword}
                        value={passwordText}
                        placeholder="Введитите пароль"
                    />
                    <div>
                        <button className="signInButton" onClick={onSignIn}>
                            sign in
                        </button>
                    </div>
                </div>
            )}
            {isAuth && (
                <div className="authContainer">
                    {/*{[].map((item) => (*/}
                    {/*    <div key={item.id} className="newsContainer">*/}
                    {/*        <div className="news">*/}
                    {/*            <p className="news_title">*/}
                    {/*                {item.articleName}*/}
                    {/*            </p>*/}
                    {/*            <div className="news_content">*/}
                    {/*                {item.text}*/}
                    {/*            </div>*/}
                    {/*        </div>*/}
                    {/*    </div>*/}
                    {/*))}*/}

                    <button className="logoutButton" onClick={onLogout}>
                        Выйти
                    </button>
                </div>
            )}
        </div>
    )
}

export default Content
